import React, { useState } from "react";
import { Routes, Route, Navigate, useLocation, useNavigate } from "react-router-dom"
import Red from "./lighter/Red";
import Yellow from "./lighter/Yellow";
import Green from "./lighter/Green";

function App() {

  const redirection = (x) => {setTimeout(() => {
    useNavigate('/yellow', {replace: false, state: {from: useLocation()}})
  }, x*100)}

  return (
    <> 
      <Routes>
        <Route path="/" element={<Navigate to="/red" replace/>}/>
        <Route path="/red" element={<Red redirection={redirection}/>}/>
        <Route path="/yellow" element={<Yellow redirection={redirection}/>}/>
        <Route path="/green" element={<Green redirection={redirection}/>}/>
      </Routes>
    </>
  );
}

export default App;
