import React, { useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom"
import Red from "./lighter/Red";
import Yellow from "./lighter/Yellow";
import Green from "./lighter/Green";

function App() {
  const timer = x => {
    const [sec, setSec] = useState(x);
	  useEffect(() => {
		  sec > 0 && setTimeout(() => setSec(sec - 1), 1000);
	  }, [sec]);
  } 

  return (
    <> 
      <Routes>
        <Route path="/" element={<Navigate to="/red" replace/>}/>
        <Route path="/red" element={<Red timer={timer}/>}/>
        <Route path="/yellow" element={<Yellow/>}/>
        <Route path="/green" element={<Green/>}/>
      </Routes>
    </>
  );
}

export default App;
