import React, { useEffect }  from "react";
import { useLocation, useNavigate } from "react-router-dom"
import './Styles.css';

const Yellow = () => {

	const navigate = useNavigate()
	const location = useLocation()

	const fromRage = location.state

	let toLocate = ''

	if (fromRage.from.pathname == null) {
		toLocate = '/red'
	}
	if (fromRage.from.pathname == '/red') {
		toLocate = '/green'
	}
	if (fromRage.from.pathname == '/green') {
		toLocate = '/red'
	}

	useEffect(() => {
		setTimeout(() => {
		  navigate(toLocate)
		}, 3000)
	  }, [])
	
	return (
		<>
		<div className="trafic">
			<div className="lighter red"></div>
			<div className="lighter yellow active"></div>
			<div className="lighter green"></div>
		</div>
		</>
	)
}

export default Yellow
