import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom"
import './Styles.css';
import { Timer } from "./Timer";


const Red = () => {
	const navigate = useNavigate()
	const location = useLocation()

	const [sec, setSec] = useState(10);

	let flick

	useEffect(() => {
		sec > 0 && setTimeout(() => {
			setSec(sec - 1)
			if (sec < 3 || sec == 3) {
				flick += "{animation: animate_fading 2s infinite; animation-timing-function: linear}"
			}

		}, 1000);
	}, [sec]);

	//переадресация
	useEffect(() => {
		setTimeout(() => {
		  navigate('/yellow', {replace: false, state: {from: location}})
		}, 10000)
	}, [])

	return (
		<>
		<div className="trafic">
			<div className="lighter red active" style={ flick }>{sec}</div>
			<div className="lighter yellow"></div>
			<div className="lighter green"></div>
		</div>	
		</>
	)
}

export default Red
